import { useState } from "react";

const BasicForm = (props) => {
  const [enteredName, setEnteredName] = useState('')
  const [enteredLastName, setEnteredLastName] = useState('')
  const [enteredEmail, setEnteredEmail] = useState('')
  const [enteredNameTouched, setenteredNameTouched] = (false)
  const [enteredLastNameTouched, setEnteredLastNameTouched] = (false)
  const [enteredEmailTouched, setEnteredEmailTouched] = (false)

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value)
  }
  const lastnameInputHandler = (event) => {
    setEnteredLastName(event.target.value) 
  }
  const emailInputHandler =  (event) => {
    setEnteredEmail(event.target.value)
  }

  const formSubmitHandler = (event) => {
    event.pre
  }

  return (
    <form>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={nameInputHandler}/>
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastnameInputHandler} />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' onChange={emailInputHandler}/>
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
