import { useRef, useState } from 'react'

const SimpleInput = () => {
	const [enteredName, setEnteredName] = useState('')
	const [enteredNameValid, setenteredNameValid] = useState(false)
	const [enteredNameTouched, setenteredNameTouched] = useState(false)
	const nameInputRef = useRef(null)

	const nameInputHandler = (event) => {  
		setEnteredName(event.target.value)
	}

	const fromSubmitHandler = (event) => {
		event.preventDefault()
		setenteredNameTouched(true)
		if (enteredName.trim() === '') {
			setenteredNameValid(false)
			return
		}
		setenteredNameValid(true)
		const enteredValueRef = nameInputRef.current.value
		console.log(enteredValueRef)
		setEnteredName('')
	}
	const nameInputClass = enteredNameValid
		? 'form-control'
		: 'form-control invalid'

	return (
		<form onSubmit={fromSubmitHandler}>
			<div className={nameInputClass}>
				{console.log(enteredName)}
				<label htmlFor='name'>Your Name</label>
				<input
				    value={enteredName}
					type='text'
					id='name'
					onChange={nameInputHandler}
					ref={nameInputRef}
				/>
				{!enteredNameValid && <p>Name must not be empty</p>}
			</div>
			<div className='form-actions'>
				<button>Submit</button>
			</div>
		</form>
	)
}

export default SimpleInput
